        // Cr√©er le commentaire en base de donn√©es
        const commentResult = await Comment.create({
            operatorCode,
            operatorName,
            lancementCode,
            comment: comment.trim()
        });
        
        if (!commentResult.success) {
            return res.status(500).json({
                success: false,
                error: commentResult.error
