// GET /api/lancements - R√©cup√©rer tous les lancements
router.get('/', async (req, res) => {
    try {
        const { search, limit = 100 } = req.query;

        let query = `
            SELECT TOP ${limit}
                t.NoEnreg,
                t.Ident,
                t.DateTravail,
                t.CodeLanctImprod as CodeLancement,
                t.Phase,
                t.CodePoste,
                t.CodeOperateur,
                l.DesignationLct1,
                l.CodeArticle
            FROM [SEDI_ERP].[GPSQL].[abetemps_temp] t
            LEFT JOIN [SEDI_ERP].[dbo].[LCTE] l ON l.CodeLancement = t.CodeLanctImprod
            WHERE 1=1
        `;

        const params = {};

        // Filtre de recherche
        if (search) {
            query += ` AND (
                t.CodeLanctImprod LIKE @search OR 
                l.DesignationLct1 LIKE @search OR 
                l.CodeArticle LIKE @search OR
                t.CodeOperateur LIKE @search
            )`;
            params.search = `%${search}%`;
        }

        query += ` ORDER BY t.DateTravail DESC, t.CodeLanctImprod`;

        const lancements = await executeQuery(query, params);

        res.json(lancements);

    } catch (error) {
        console.error('Erreur lors de la r√©cup√©ration des lancements:', error);
        res.status(500).json({ 
            error: 'Erreur lors de la r√©cup√©ration des lancements' 
        });
    }
});

