        console.log(`üîç R√©cup√©ration de l'historique pour l'op√©rateur ${operatorCode}...`);

        // R√©cup√©rer tous les √©v√©nements de cet op√©rateur depuis ABHISTORIQUE_OPERATEURS avec la phase depuis abetemps
        const eventsQuery = `
            SELECT 
                h.NoEnreg,
                h.Ident,
