const express = require('express');
const cors = require('cors');
const helmet = require('helmet');
const morgan = require('morgan');
const rateLimit = require('express-rate-limit');
require('dotenv').config();

const dbConfig = require('./config/database');
const operatorRoutes = require('./routes/operators');
const lancementRoutes = require('./routes/lancements');
const operationRoutes = require('./routes/operations');
const adminRoutes = require('./routes/admin');
const authRoutes = require('./routes/auth');
const commentRoutes = require('./routes/comments');

const app = express();
const PORT = process.env.PORT || 3001;

// Middleware de s√©curit√©
app.use(helmet());

// Ensure CORS headers are present on all responses (including errors)
// and reply to OPTIONS preflight requests even if later middleware/errors occur.
app.use((req, res, next) => {
    const origin = req.headers.origin;
    if (origin) {
        // Echo back the origin so browsers accept credentialed responses
        res.setHeader('Access-Control-Allow-Origin', origin);
        // Inform caches that response varies by origin
        res.setHeader('Vary', 'Origin');
    }
    res.setHeader('Access-Control-Allow-Credentials', 'true');
    res.setHeader('Access-Control-Allow-Methods', 'GET,HEAD,PUT,PATCH,POST,DELETE,OPTIONS');
    res.setHeader('Access-Control-Allow-Headers', 'Origin, X-Requested-With, Content-Type, Accept, Authorization');

    if (req.method === 'OPTIONS') {
        // short-circuit preflight
        return res.status(204).end();
    }

    next();
});

app.use(cors({
    origin: [
        'http://localhost:8080',
        'http://127.0.0.1:8080',
        'http://localhost:3001',
        'http://127.0.0.1:3001',
        'http://192.168.1.26:8080',
        'http://192.168.1.26:3001',
        process.env.FRONTEND_URL
    ].filter(Boolean),
    credentials: true
}));

// Rate limiting - plus permissif en d√©veloppement
const limiter = rateLimit({
    windowMs: 15 * 60 * 1000, // 15 minutes
    max: process.env.NODE_ENV === 'production' ? 100 : 1000, // 100 en prod, 1000 en dev
    message: {
        error: 'Trop de requ√™tes, veuillez patienter',
        retryAfter: Math.ceil(15 * 60 * 1000 / 1000) // en secondes
    },
    standardHeaders: true,
    legacyHeaders: false,
    skip: (req) => {
        // Skip rate limiting pour les requ√™tes de sant√©
        return req.path === '/api/health';
    }
});
app.use(limiter);

// Middleware de parsing
app.use(express.json({ limit: '10mb' }));
app.use(express.urlencoded({ extended: true }));

// Logging
app.use(morgan('combined'));

// Rate limiting sp√©cifique pour les routes admin (plus permissif)
const adminLimiter = rateLimit({
    windowMs: 1 * 60 * 1000, // 1 minute
    max: process.env.NODE_ENV === 'production' ? 200 : 500, // 200 en prod, 500 en dev
    message: {
        error: 'Trop de requ√™tes admin, veuillez patienter',
        retryAfter: 60
    },
    skip: (req) => {
        // Skip pour les requ√™tes de sant√© et les requ√™tes de lecture
        return req.path === '/api/health' || req.method === 'GET';
    }
});

// Routes
app.use('/api/auth', authRoutes);
app.use('/api/operators', operatorRoutes);
app.use('/api/lancements', lancementRoutes);
app.use('/api/operations', operationRoutes);
app.use('/api/comments', commentRoutes);
app.use('/api/admin', adminLimiter, adminRoutes);

// Route de sant√©
app.get('/api/health', (req, res) => {
    res.json({ 
        status: 'OK', 
        timestamp: new Date().toISOString(),
        uptime: process.uptime()
    });
});

// Route racine
app.get('/', (req, res) => {
    res.json({ 
        message: 'SEDI Tablette API',
        version: '1.0.0',
        endpoints: {
            health: '/api/health',
            operators: '/api/operators',
            lancements: '/api/lancements',
            operations: '/api/operations',
            comments: '/api/comments',
            admin: '/api/admin'
        }
    });
});

// Gestion des erreurs 404
app.use('*', (req, res) => {
    res.status(404).json({ 
        error: 'Endpoint non trouv√©',
        path: req.originalUrl
    });
});

// Gestion globale des erreurs
app.use((err, req, res, next) => {
    console.error('Erreur serveur:', err);

    res.status(err.status || 500).json({
        error: process.env.NODE_ENV === 'production' 
            ? 'Erreur interne du serveur' 
            : err.message,
        ...(process.env.NODE_ENV !== 'production' && { stack: err.stack })
    });
});

// D√©marrage du serveur (seulement si ce n'est pas un test)
// V√©rifier NODE_ENV au moment de l'ex√©cution, pas de l'import
const shouldStartServer = () => {
    // Ne pas d√©marrer le serveur si NODE_ENV est 'test' ou si on est dans un contexte de test
    return process.env.NODE_ENV !== 'test' && 
           process.env.NODE_ENV !== 'testing' &&
           !process.env.JEST_WORKER_ID && // Jest utilise cette variable
           !process.argv.some(arg => arg.includes('jest')); // V√©rifier si jest est dans les arguments
};

// Stocker la r√©f√©rence du serveur pour pouvoir le fermer proprement
let server = null;

if (shouldStartServer()) {
    server = app.listen(PORT, () => {
        console.log(`üöÄ Serveur SEDI Tablette d√©marr√© sur le port ${PORT}`);
        console.log(`üìä Interface admin: http://localhost:${PORT}/api/admin`);
        console.log(`üîç Sant√©: http://localhost:${PORT}/api/health`);
    });
} else {
    console.log('üß™ Mode test d√©tect√© - Serveur non d√©marr√©');
}

// Gestion propre de l'arr√™t
process.on('SIGTERM', () => {
    console.log('üõë Arr√™t du serveur...');
    if (server) {
        server.close(() => {
            console.log('‚úÖ Serveur ferm√© proprement');
            process.exit(0);
        });
    } else {
        process.exit(0);
    }
});

process.on('SIGINT', () => {
    console.log('üõë Arr√™t du serveur...');
    if (server) {
        server.close(() => {
            console.log('‚úÖ Serveur ferm√© proprement');
            process.exit(0);
        });
    } else {
        process.exit(0);
    }
});

// Fonction pour fermer le serveur proprement (utile pour les tests)
const closeServer = () => {
    return new Promise((resolve) => {
        if (server) {
            server.close(() => {
                console.log('‚úÖ Serveur ferm√© proprement');
                resolve();
            });
        } else {
            resolve();
        }
    });
};

