  timeout: 10000,
  verbose: true
};

// Classe de test simple
class TestRunner {
  constructor() {
    this.tests = [];
    this.passed = 0;
    this.failed = 0;
    this.startTime = Date.now();
  }

  describe(name, fn) {
    console.log(`\nüìã ${name}`);
    fn();
  }

  it(name, fn) {
    this.tests.push({ name, fn });
  }

  async run() {
    console.log('üöÄ D√©marrage des tests SEDI Tablette v2...\n');
    
    for (const test of this.tests) {
      try {
        console.log(`  ‚úì ${test.name}`);
        await test.fn();
        this.passed++;
      } catch (error) {
        console.log(`  ‚ùå ${test.name}`);
        console.log(`     Erreur: ${error.message}`);
        this.failed++;
      }
    }

    this.printSummary();
  }

  printSummary() {
    const duration = Date.now() - this.startTime;
    console.log(`\nüìä R√©sum√© des tests:`);
    console.log(`   ‚úÖ R√©ussis: ${this.passed}`);
    console.log(`   ‚ùå √âchou√©s: ${this.failed}`);
    console.log(`   ‚è±Ô∏è  Dur√©e: ${duration}ms`);
    console.log(`   üìà Taux de r√©ussite: ${((this.passed / (this.passed + this.failed)) * 100).toFixed(1)}%`);
  }
}

// Fonctions d'assertion
const expect = (actual) => ({
  toBe: (expected) => {
    if (actual !== expected) {
      throw new Error(`Expected ${actual} to be ${expected}`);
    }
  },
  toEqual: (expected) => {
    if (JSON.stringify(actual) !== JSON.stringify(expected)) {
      throw new Error(`Expected ${JSON.stringify(actual)} to equal ${JSON.stringify(expected)}`);
    }
  },
  toThrow: (expectedError) => {
    try {
      if (typeof actual === 'function') {
        actual();
      }
      throw new Error('Expected function to throw an error');
    } catch (error) {
      if (expectedError && !error.message.includes(expectedError)) {
        throw new Error(`Expected error message to contain "${expectedError}", got "${error.message}"`);
      }
    }
  },
  toHaveBeenCalled: () => {
    if (!actual.called) {
      throw new Error('Expected function to have been called');
    }
  },
  toHaveBeenCalledWith: (...args) => {
    if (!actual.called || !actual.calledWith) {
      throw new Error('Expected function to have been called');
    }
    if (JSON.stringify(actual.calledWith) !== JSON.stringify(args)) {
      throw new Error(`Expected function to have been called with ${JSON.stringify(args)}, got ${JSON.stringify(actual.calledWith)}`);
    }
  }
});

// Mock simple
const mock = (fn) => {
  const mockFn = (...args) => {
    mockFn.called = true;
    mockFn.calledWith = args;
    return mockFn.returnValue;
  };
  mockFn.called = false;
  mockFn.calledWith = null;
  mockFn.returnValue = undefined;
  mockFn.mockReturnValue = (value) => {
    mockFn.returnValue = value;
    return mockFn;
  };
  return mockFn;
};

// Export pour utilisation
module.exports = { TestRunner, expect, mock };

